package com.tnsf.daytwo;

public class ForeachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {10,20,30,40,50};
		for(int i:a)
		{
			System.out.println(i+ " ");
		}
		
		char ch[]= {'A','B','C'};
		for(char i:ch)
		{
			System.out.println(i+ " ");
		}
	}

}
